NO_OF_CHARS = 256

def func(string): 
    '''
    Write a function to accurately remove characters from the first string which are present in the second string.
    '''
    if len(string) < 2:
        return string

    # Create a list to store characters to be removed
    remove_chars = [False] * NO_OF_CHARS

    # Mark characters in second string
    for char in string[1]:
        remove_chars[ord(char)] = True

    # Create a list to store characters that are not in second string
    result = []
    for char in string[0]:
        if not remove_chars[ord(char)]:
            result.append(char)

    return ''.join(result)
