    if not nums1 or not nums2:
        return []

    for num1 in nums1:
        for num2 in nums2:
            if len(min_heap) < k:
                heapq.heappush(min_heap, (num1 + num2, num1, num2))
            else:
                if num1 + num2 < min_heap[0][0]:
                    heapq.heappop(min_heap)
                    heapq.heappush(min_heap, (num1 + num2, num1, num2))
                else:
                    break

    result = []
    for _ in range(k):
        if min_heap:
            _, num1, num2 = heapq.heappop(min_heap)
            result.append((num1, num2))
